import React, { Component } from 'react';
import ExisitingPlansCard from "../components/exisitingplancard"

class ExisitingPlans extends Component {
    constructor(props) {
        super(props);
        this.state = {
            storedUserDetails: null,
            newSavingTarget: this.props.newSavingTarget
          }
    }

    mapSavingPlans = () => 
        this.props.storedUserDetails.saving_targets.map(savingTargets => <ExisitingPlansCard savingTargets={savingTargets} />)

    updatestoredUserDetails = () => {
        console.log(this.props.newSavingTarget)
        console.log(this.state.storedUserDetails)
        // this.setState({ storedUserDetails: this.state.storedUserDetails.saving_targets = this.props })
    }


    componentDidUpdate() {
        const userID = this.props.storedUserDetails.id
        fetch(`http://localhost:3000/api/v1/users/${userID}`)
            .then(resp => resp.json())
            .then(userObject => this.setState({
                storedUserDetails: userObject.user
            }))


    }

    render() { 
        this.updatestoredUserDetails()

        const { storedUserDetails } = this.state
        this.mapSavingPlans()

        return ( 
                <div class="ui grid">
                    {this.mapSavingPlans()}
                </div>
        )
    }
}

export default ExisitingPlans;
