import React, { Component } from 'react'
import {
    DateInput,
    TimeInput,
    DateTimeInput,
    DatesRangeInput
} from 'semantic-ui-calendar-react';

class SavingsForm extends Component {
    constructor(props) {
        super(props);

        this.state = {
            date: '',
            time: '',
            dateTime: '',
            datesRange: ''
        };
    }

    handleChange = (event, { name, value }) => {
        if (this.state.hasOwnProperty(name)) {
            this.setState({ [name]: value });
        }
    }

    render() { 
        return ( 
            <div class="ui one column stackable center aligned page grid">
                <div class="column ten wide">
                    <h3 className="">demo-savings-form</h3>
                    <form className="ui large form" onSubmit={() => this.handleSubmit()}>
                        <div className="segment">
                                <div class="field">
                            <label className="ui left">Savings Category</label>
                                <input placeholder="Category" type="text" />
                            </div>
                            <div class="field">
                            <label>Image Url</label>
                                <input placeholder="Image link" type="text" />
                            </div>
                        </div>
                        <div class="field">
                            <label>Timescale</label>

                        <DatesRangeInput
                            name="datesRange"
                            placeholder="From - To"
                            value={this.state.datesRange}
                            iconPosition="left"
                            onChange={this.handleChange} />
                        </div>

                        <div className="ui fluid large black submit button" onClick={() => this.handleSubmit()}>
                            Submit
                        </div> 
                    </form>
                </div>
            </div>





         )
    }
}

export default SavingsForm
