import React, { Component } from 'react'
import { Modal, Button, Comment, Form, Header } from 'semantic-ui-react'
import CommentCard from "../components/commentcard"

class MapComments extends Component {

    constructor(props) {
        super(props)
        this.state = {  }
    }

    userFromId = (id) => {
        const deepCopy = JSON.parse(JSON.stringify(this.props.userSavingTargets))
        deepCopy.filter

    }

    mapComments = () => {
        return this.props.userSavingTargets.comments.map(comment => <CommentCard comment={comment.text} date={comment.created_at} />   )
    }

    componentDidMount() {
        fetch("http://localhost:3000/api/v1/users")
            .then(data => data.json())
            .then(users => this.setState({ users }))
    }

    render() { 
        const { open, close } = this.props
        return (
            <Modal dimmer="blurring" open={open} onClose={close} closeOnDimmerClick={false} centered={false}>
                <Modal.Content>

                    <Comment.Group>
                        <Header as='h3' dividing>
                            Comments
                        </Header>


                        {this.props.userSavingTargets?
                                this.mapComments()
                                :
                                null
                            }
                        <Form reply>
                            <Form.TextArea />
                            <Button content='Add Reply' labelPosition='left' icon='edit' primary />
                        </Form>
                    </Comment.Group>
                </Modal.Content>
                <Modal.Actions>
                    <Button
                        primary
                        icon='checkmark'
                        labelPosition='right'
                        content="Close"
                        onClick={close}
                    />
                </Modal.Actions>
            </Modal>
          )
    }
}

export default MapComments
