import React, { Component } from 'react';
import ExisitingPlansCard from "../components/exisitingplancard"

class ExisitingPlans extends Component {
    constructor(props) {
        super(props);
        this.state = {
            newSavingTarget: this.props.newSavingTarget
          }
    }

    mapSavingPlans = () => {
        const copyArray = this.state.storedUserDetails.saving_targets
        console.log(copyArray)
        return copyArray.map(savingTargets => <ExisitingPlansCard savingTargets={savingTargets} />)
    }
    // most likely will have to rethink of how to do this, as you dont have access to date creation
    newSavingTargets = () => {
        if (this.props.newSavingTarget) {
            const deepStoredProps = JSON.parse(JSON.stringify(this.props.storedUserDetails))
            const deepSavingTarget = JSON.parse(JSON.stringify(this.props.newSavingTarget))


            deepStoredProps.saving_targets.push(deepSavingTarget[0])
            deepStoredProps.user_saving_targets.push(deepSavingTarget[1])
            console.log(deepStoredProps)
            this.setState({storedUserDetails: "deepStoredProps"})

            return deepStoredProps
        } else {
            console.log(this.props.storedUserDetails)
            return this.props.storedUserDetails
        }
    }

    componentWillMount() {

        this.setState({
            storedUserDetails: this.newSavingTargets()
        })
    }

    render() { 

        const { storedUserDetails } = this.state

        return ( 
                <div className="ui grid">
                {storedUserDetails?
                    this.mapSavingPlans()
                    :
                    <p>loading</p>
                    }
                </div>
        )
    }
}

export default ExisitingPlans
