import React, { Component } from 'react';
import ExisitingPlansCard from "../components/exisitingplancard"

class ExisitingPlans extends Component {
    constructor(props) {
        super(props);
        this.state = {
            newSavingTarget: this.props.newSavingTarget
          }
    }

    mapSavingPlans = () => {
        const copyArray = this.state.storedUserDetails.saving_targets
        console.log(copyArray)
        // return copyArray.map(savingTargets => <ExisitingPlansCard savingTargets={savingTargets} />)
    }

    newSavingTargets = () => {
        if (this.props.newSavingTarget) {
            const copyStoredProps = [...this.props.storedUserDetails.saving_targets]
            const deepSavingTarget = JSON.parse(JSON.stringify(this.props.newSavingTarget))


            copyStoredProps.push(deepSavingTarget)
            console.log(copyStoredProps)

            return copyStoredProps
        } else {

            return this.props.storedUserDetails
        }
    }

    componentWillMount() {

        this.setState({
            storedUserDetails: this.newSavingTargets()
        })
    }

    render() { 

        const { storedUserDetails } = this.state

        return ( 
                <div className="ui grid">
                {storedUserDetails?
                    this.mapSavingPlans()
                    :
                    <p>loading</p>
                    }
                </div>
        )
    }
}

export default ExisitingPlans
