import React, { Component } from 'react';
import API from "../API"

class exisitingPlanCard extends Component {
    constructor(props) {

        super(props);
        this.state = {
            userSavingTargets: undefined
         }
    }


    formatDate = () => {
        const oneDay = 24 * 60 * 60 * 1000; // hours*minutes*seconds*milliseconds
        const date1 = new Date(Date.now())
        const date2 = new Date(this.props.savingTargets.end_date)
        const daysToGo = Math.round(Math.abs((date2.getTime() - date1.getTime()) / (oneDay)))
        if (daysToGo > 0) {
            return Math.round(Math.abs((date2.getTime() - date1.getTime()) / (oneDay)))
        } else {
            return "This plan has expired!"
        }
    }

    convertDateToString = () => 
         new Date(Date.parse(this.props.savingTargets.start_date)).toDateString()

    filterAmounts = (amounts) => {
        const savingTargetId = this.props.savingTargets.id
        console.log(savingTargetId)

        return amounts.filter(amount => amount.id === savingTargetId )

    }



    componentWillMount() {
        fetch("http://localhost:3000/api/v1/user_saving_targets")
            .then(data => data.json())
            .then(savingTargets => this.setState({ userSavingTargets: this.filterAmounts(savingTargets) }))
        // this.setState({
